// Initialize 2 variables and find the sum difference product quotient and remainder without using static method
public class ArithmeticOperationsWithoutStaticMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int num1 = 10;
	        int num2 = 4;

	        // Calculate sum
	        int sum = num1 + num2;

	        // Calculate difference
	        int difference = num1 - num2;

	        // Calculate product
	        int product = num1 * num2;

	        // Calculate quotient
	        int quotient = num1 / num2;

	        // Calculate remainder
	        int remainder = num1 % num2;

	        // Display results
	        System.out.println("Without using static methods:");
	        System.out.println("Sum: " + sum);
	        System.out.println("Difference: " + difference);
	        System.out.println("Product: " + product);
	        System.out.println("Quotient: " + quotient);
	        System.out.println("Remainder: " + remainder);

	}

}
